    return (
        <div className="slide-container">
            
            {dataSlider.map((obj,index) =>{
                return(
                    <div className="slide">
                        <img 
                            src={process.env.PUBLIC_URL+`imgs/slider_${index+1}.jpg`}  
                        />
                    </div>
                )})}    
            <BtnSlider/>
            <BtnSlider/>
        </div>
        
    )
}
