import React from "react";
// hooks
import useScript from '../hooks/useScript';
// Font Awesome
import './FontAwesome';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';

function Slider(){
    useScript('../scripts/slider.js');
    return(
        <div className="slider">
            <i className="fa fa-angle-left slider-prev" ></i>
            <ul className="slider-dots">
                <li className="slider-dot-item active" data-index="0"></li>
                <li className="slider-dot-item" data-index="1"></li>
                <li className="slider-dot-item" data-index="2"></li>
            </ul>
            <div className="slider-wrapper">
                <div className="slider-main">
                    <div className="slider-item">
                        <img src="https://images.unsplash.com/photo-1475809913362-28a064062ccd?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80" alt="" />
                    </div>
                    <div class="slider-item">
                        <img src="https://images.unsplash.com/photo-1463501810073-6e31c827a9bc?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80" alt=""/>
                    </div>
                    <div class="slider-item">
                    <img
                    src="https://images.unsplash.com/photo-1497752531616-c3afd9760a11?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80"
                    alt=""
                    />
                </div>
                </div>
            </div>
           
            <i className="fa fa-angle-right slider-next"></i>
        </div>
    )
}

export default Slider;
