import React from "react";
// hooks
import useScript from '../hooks/useScript';
// Font Awesome

function Slider(){

    const slider =document.querySelector(".slider");
    const sliderMain =document.querySelector(".slider-main");
    const sliderItems =document.querySelectorAll(".slider-item");
    const nextBtn =document.querySelector(".slider-next");
    const prevBtn =document.querySelector(".slider-prev");
    const dotItem =document.querySelectorAll(".slider-dots-item")
    const sliderItemWidth = sliderItems[0].offsetWidth;

    // JSX
    return(
        <div className="slider">
            <i className="fa fa-angle-left slider-prev" ></i>
            <ul className="slider-dots">
                <li className="slider-dots-item active" data-index="0"></li>
                <li className="slider-dots-item" data-index="1"></li>
                <li className="slider-dots-item" data-index="2"></li>
            </ul>
            <div className="slider-wrapper">
                <div className="slider-main">
                    <div className="slider-item">
                        <img src={process.env.PUBLIC_URL +"./slider_1.jpg"} alt="" />
                    </div>
                    <div className="slider-item">
                        <img src="https://images.unsplash.com/photo-1463501810073-6e31c827a9bc?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80" alt=""/>
                    </div>
                    <div className="slider-item">
                        <img src="https://images.unsplash.com/photo-1497752531616-c3afd9760a11?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80" alt=""/>
                    </div>
                </div>
            </div>
           
            <i className="fa fa-angle-right slider-next"></i>
        </div>
    )
}

export default Slider;
